import{_ as l,c as t,o as i,ag as e}from"./chunks/framework.ko2zIC2c.js";const g=JSON.parse('{"title":"游戏学习路线","description":"游戏学习路线, Unity3D学习路线, Cocos学习路线, LayaAir学习路线","frontmatter":{"title":"游戏学习路线","description":"游戏学习路线, Unity3D学习路线, Cocos学习路线, LayaAir学习路线"},"headers":[],"relativePath":"docs/road/core/game.md","filePath":"docs/road/core/game.md","lastUpdated":1753387362000}'),r={name:"docs/road/core/game.md"};function o(n,a,d,s,h,c){return i(),t("div",null,a[0]||(a[0]=[e('<h1 id="游戏学习路线" tabindex="-1">游戏学习路线 <a class="header-anchor" href="#游戏学习路线" aria-label="Permalink to &quot;游戏学习路线&quot;">​</a></h1><p>游戏开发是一个多学科的领域，结合了编程、设计、艺术和音频制作。以下是游戏开发的全面介绍：</p><h2 id="游戏开发核心领域" tabindex="-1">游戏开发核心领域 <a class="header-anchor" href="#游戏开发核心领域" aria-label="Permalink to &quot;游戏开发核心领域&quot;">​</a></h2><h3 id="_1-游戏引擎" tabindex="-1">1. 游戏引擎 <a class="header-anchor" href="#_1-游戏引擎" aria-label="Permalink to &quot;1. 游戏引擎&quot;">​</a></h3><ul><li><strong>主流引擎</strong>： <ul><li>Unity (C#) - 适合2D/3D跨平台开发</li><li>Unreal Engine (C++/蓝图) - 3A级3D游戏首选</li><li>Godot (GDScript/C#) - 开源轻量级引擎</li><li>Cocos2d-x (C++/Lua/JS) - 2D移动游戏</li><li>自研引擎 - 大厂专用(如寒霜引擎、RE引擎)</li></ul></li></ul><h3 id="_2-编程语言" tabindex="-1">2. 编程语言 <a class="header-anchor" href="#_2-编程语言" aria-label="Permalink to &quot;2. 编程语言&quot;">​</a></h3><ul><li><strong>常用语言</strong>： <ul><li>C++ (高性能游戏)</li><li>C# (Unity开发)</li><li>Python (工具/脚本)</li><li>Lua (游戏逻辑/热更新)</li><li>JavaScript/TypeScript (网页游戏)</li></ul></li></ul><h3 id="_3-游戏类型与技术" tabindex="-1">3. 游戏类型与技术 <a class="header-anchor" href="#_3-游戏类型与技术" aria-label="Permalink to &quot;3. 游戏类型与技术&quot;">​</a></h3><ul><li><strong>2D游戏</strong>：精灵动画、瓦片地图、物理引擎</li><li><strong>3D游戏</strong>：建模、光照、着色器、粒子系统</li><li><strong>VR/AR游戏</strong>：Unity XR/Unreal VR框架</li><li><strong>网络游戏</strong>：多人同步、服务器架构</li></ul><h2 id="游戏开发全流程" tabindex="-1">游戏开发全流程 <a class="header-anchor" href="#游戏开发全流程" aria-label="Permalink to &quot;游戏开发全流程&quot;">​</a></h2><h3 id="_1-预生产阶段" tabindex="-1">1. 预生产阶段 <a class="header-anchor" href="#_1-预生产阶段" aria-label="Permalink to &quot;1. 预生产阶段&quot;">​</a></h3><ul><li>概念设计</li><li>原型开发</li><li>技术可行性验证</li></ul><h3 id="_2-生产阶段" tabindex="-1">2. 生产阶段 <a class="header-anchor" href="#_2-生产阶段" aria-label="Permalink to &quot;2. 生产阶段&quot;">​</a></h3><ul><li><p><strong>核心系统</strong>：</p><ul><li>游戏循环</li><li>物理系统</li><li>AI系统</li><li>存档系统</li><li>UI系统</li></ul></li><li><p><strong>内容创作</strong>：</p><ul><li>关卡设计</li><li>角色/道具建模</li><li>动画制作</li><li>音效/配乐</li></ul></li></ul><h3 id="_3-后期阶段" tabindex="-1">3. 后期阶段 <a class="header-anchor" href="#_3-后期阶段" aria-label="Permalink to &quot;3. 后期阶段&quot;">​</a></h3><ul><li>性能优化</li><li>测试与调试</li><li>发布与运营</li></ul><h2 id="学习路径建议" tabindex="-1">学习路径建议 <a class="header-anchor" href="#学习路径建议" aria-label="Permalink to &quot;学习路径建议&quot;">​</a></h2><h3 id="初学者路线" tabindex="-1">初学者路线 <a class="header-anchor" href="#初学者路线" aria-label="Permalink to &quot;初学者路线&quot;">​</a></h3><ol><li>选择引擎(推荐Unity或Unreal)</li><li>完成官方入门教程</li><li>开发简单小游戏(如贪吃蛇、平台跳跃)</li><li>逐步增加复杂度</li></ol><h3 id="进阶方向" tabindex="-1">进阶方向 <a class="header-anchor" href="#进阶方向" aria-label="Permalink to &quot;进阶方向&quot;">​</a></h3><ul><li><strong>图形编程</strong>：OpenGL/DirectX/Vulkan</li><li><strong>游戏物理</strong>：刚体动力学、碰撞检测</li><li><strong>游戏AI</strong>：行为树、状态机、寻路算法</li><li><strong>网络同步</strong>：状态同步、帧同步</li></ul><h2 id="资源推荐" tabindex="-1">资源推荐 <a class="header-anchor" href="#资源推荐" aria-label="Permalink to &quot;资源推荐&quot;">​</a></h2><ul><li><strong>教程</strong>：Unity Learn, Unreal官方文档</li><li><strong>社区</strong>：GameDev.net, 独立游戏吧</li><li><strong>工具</strong>：Blender(3D建模), Aseprite(像素艺术), FMOD/Wwise(音频)</li></ul><h2 id="学习文档" tabindex="-1">学习文档 <a class="header-anchor" href="#学习文档" aria-label="Permalink to &quot;学习文档&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">引擎</th><th>简介</th><th>难度</th></tr></thead><tbody><tr><td style="text-align:left;"><a href="/docs/game/unity/">Unity3D</a></td><td><code>Unity3D</code>是一款跨平台的游戏引擎和开发工具，广泛用于游戏开发、虚拟现实(VR)、增强现实(AR)和3D交互应用开发</td><td>🌟🌟🌟</td></tr><tr><td style="text-align:left;"><a href="/docs/game/layaair/">LayaAir</a></td><td><code>LayaAir</code>是一款国产的HTML5游戏引擎，支持2D、3D、VR等多种游戏开发，具有高性能、跨平台的特点，特别适合微信小游戏、H5游戏开发</td><td>🌟🌟</td></tr><tr><td style="text-align:left;"><a href="/docs/game/cocos/">Cocos</a></td><td><code>Cocos Creator</code>是一款国产的跨平台游戏开发工具，基于 Cocos2d-x 引擎，支持 2D 和 3D 游戏开发，具有可视化编辑和组件化开发的特点</td><td>🌟🌟</td></tr></tbody></table>',25)]))}const b=l(r,[["render",o]]);export{g as __pageData,b as default};
