import{_ as o,c,o as a,ag as d}from"./chunks/framework.ko2zIC2c.js";const f=JSON.parse('{"title":"React 前言","description":"React, React.js, React教程, React从入门到精通","frontmatter":{"title":"React 前言","description":"React, React.js, React教程, React从入门到精通"},"headers":[],"relativePath":"docs/frontend/react/index.md","filePath":"docs/frontend/react/index.md","lastUpdated":1754411669000}'),t={name:"docs/frontend/react/index.md"};function i(l,e,r,n,s,p){return a(),c("div",null,e[0]||(e[0]=[d('<h1 id="前言" tabindex="-1">前言 <a class="header-anchor" href="#前言" aria-label="Permalink to &quot;前言&quot;">​</a></h1><p><code>React</code>官网: <a href="https://zh-hans.react.dev/" target="_blank" rel="noreferrer">https://zh-hans.react.dev/</a></p><p><code>React</code>是用于构建用户界面的<code>JavaScript</code>库, 将数据渲染为<code>HTML</code>视图的开源<code>JavaScript</code>库</p><h2 id="简介" tabindex="-1">简介 <a class="header-anchor" href="#简介" aria-label="Permalink to &quot;简介&quot;">​</a></h2><p>由<code>Facebook</code>开发且开源</p><ol><li>起初由<code>Fackbook</code>的软件工程师<code>Jordan Walke</code>创建</li><li>于<code>2011</code>年部署于<code>Facebook</code>的<code>newsfeed</code></li><li>随后在<code>2012</code>年部署于<code>Instagram</code></li><li><code>2013</code>年<code>5</code>月宣布开源</li></ol><p>近十年陈酿, <code>React</code>正在被腾讯, 阿里等一线大厂广泛使用</p><h2 id="为什么要学习-react" tabindex="-1">为什么要学习 React <a class="header-anchor" href="#为什么要学习-react" aria-label="Permalink to &quot;为什么要学习 React&quot;">​</a></h2><p>原生<code>JavaScript</code>的痛点:</p><ol><li>原生<code>JavaScript</code>操作<code>DOM</code>繁琐, 效率低</li><li>使用<code>JavaScript</code>直接操作<code>DOM</code>, 浏览器会进行大量的<strong>重绘重排</strong></li><li>原生<code>JavaScript</code>没有组件化编码方案, 代码复用率低</li></ol><p><code>React</code>的特点:</p><ol><li>采用<strong>组件化</strong>模式, <strong>声明式编码</strong>, 提高开发效率且组件复用率</li><li>在<code>React Native</code>中可以使用<code>React</code>语法进行<strong>移动端开发</strong></li><li>使用<code>虚拟DOM</code> ➕ 优秀的<code>Diffing</code>算法, 尽量减少与真实<code>DOM</code>的交互</li></ol><h2 id="react-高效的原因" tabindex="-1">React 高效的原因 <a class="header-anchor" href="#react-高效的原因" aria-label="Permalink to &quot;React 高效的原因&quot;">​</a></h2><ol><li>使用虚拟<code>DOM</code>, 不直接操作页面上的真实<code>DOM</code></li><li><code>DOM Diffing</code>算法, 最小化减少页面重绘</li></ol><h2 id="学习要求" tabindex="-1">学习要求 <a class="header-anchor" href="#学习要求" aria-label="Permalink to &quot;学习要求&quot;">​</a></h2><p>学习<code>React</code>之前需要掌握的<code>JavaScript</code>基础知识</p><ol><li>判断<code>this</code>的指向</li><li><code>Class</code>类</li><li><code>ES6</code>语法规范</li><li><code>npm</code>包管理器</li><li>原型, 原型链</li><li>数组常用方法</li><li>模块化</li></ol>',17)]))}const R=o(t,[["render",i]]);export{f as __pageData,R as default};
