import{_ as o,c as r,o as e,ag as d}from"./chunks/framework.ko2zIC2c.js";const h=JSON.parse('{"title":"Vue2 计算属性与监视属性的区别","description":"Vue2, Vue2计算属性与监视属性的区别, Vue.js, Vue2教程, Vue2从入门到精通","frontmatter":{"title":"Vue2 计算属性与监视属性的区别","description":"Vue2, Vue2计算属性与监视属性的区别, Vue.js, Vue2教程, Vue2从入门到精通"},"headers":[],"relativePath":"docs/frontend/vue/core/v2/06-computed-watch.md","filePath":"docs/frontend/vue/core/v2/06-computed-watch.md","lastUpdated":1754039919000}'),n={name:"docs/frontend/vue/core/v2/06-computed-watch.md"};function s(a,t,c,l,g,i){return e(),r("div",null,t[0]||(t[0]=[d('<h1 id="计算属性与监视属性的区别" tabindex="-1">计算属性与监视属性的区别 <a class="header-anchor" href="#计算属性与监视属性的区别" aria-label="Permalink to &quot;计算属性与监视属性的区别&quot;">​</a></h1><p><code>computed</code>能完成的功能, <code>watch</code>都可以完成</p><p><code>watch</code>能完成的功能, <code>computed</code>不一定能完成, 如<code>watch</code>可以进行异步操作</p><h2 id="主要区别" tabindex="-1">主要区别 <a class="header-anchor" href="#主要区别" aria-label="Permalink to &quot;主要区别&quot;">​</a></h2><ul><li>计算属性是为了<strong>计算新值</strong>, 而监视属性是为了<strong>响应数据变化执行操作</strong></li><li>计算属性必须要有<strong>返回值</strong>, 而监视属性<strong>不需要</strong></li><li>计算属性有<strong>缓存</strong>, 依赖数据不变不会重新计算, 而监视属性<strong>没有缓存</strong>, 每次数据变化都会执行</li><li>计算属性<strong>不支持异步</strong>, 监视属性<strong>支持异步</strong></li><li>计算属性使用<strong>声明式</strong>方式, 监视属性使用<strong>命名式</strong></li><li>计算属性用于<strong>模板中的派生数据</strong>, 监视属性用于<strong>数据变化时的副作用</strong></li></ul><table tabindex="0"><thead><tr><th>特性</th><th>计算属性 (Computed)</th><th>监视属性 (Watch)</th></tr></thead><tbody><tr><td><strong>目的</strong></td><td>计算新值</td><td>响应变化执行操作</td></tr><tr><td><strong>返回值</strong></td><td>必须有</td><td>不需要</td></tr><tr><td><strong>缓存</strong></td><td>有缓存，依赖不变不重新计算</td><td>无缓存，每次变化都执行</td></tr><tr><td><strong>异步</strong></td><td>不支持</td><td>支持</td></tr><tr><td><strong>使用方式</strong></td><td>声明式</td><td>命令式</td></tr><tr><td><strong>适合场景</strong></td><td>模板中的派生数据</td><td>数据变化时的副作用</td></tr></tbody></table><h2 id="使用建议" tabindex="-1">使用建议 <a class="header-anchor" href="#使用建议" aria-label="Permalink to &quot;使用建议&quot;">​</a></h2><ul><li>当需要根据其他数据计算一个值时, 使用<strong>计算属性</strong></li><li>当需要在数据变化时执行异步或开销较大的操作时, 使用<strong>监视属性</strong></li></ul><p>大多数情况下, 计算属性是更优选择, 因为<strong>计算属性</strong>的声明式特性和缓存机制更高效</p>',9)]))}const p=o(n,[["render",s]]);export{h as __pageData,p as default};
