import{_ as l,c as o,o as r,ag as e}from"./chunks/framework.ko2zIC2c.js";const u=JSON.parse('{"title":"后端学习路线","description":"后端学习路线, PHP学习路线, Node学习路线, Java学习路线, Golang学习路线","frontmatter":{"title":"后端学习路线","description":"后端学习路线, PHP学习路线, Node学习路线, Java学习路线, Golang学习路线"},"headers":[],"relativePath":"docs/road/core/backend.md","filePath":"docs/road/core/backend.md","lastUpdated":1753665350000}'),i={name:"docs/road/core/backend.md"};function t(n,a,s,d,h,c){return r(),o("div",null,a[0]||(a[0]=[e('<h1 id="后端学习路线" tabindex="-1">后端学习路线 <a class="header-anchor" href="#后端学习路线" aria-label="Permalink to &quot;后端学习路线&quot;">​</a></h1><p>后端开发是指构建和维护服务器、数据库和应用程序逻辑的部分，负责处理前端请求、业务逻辑实现和数据存储。</p><h2 id="核心技术" tabindex="-1">核心技术 <a class="header-anchor" href="#核心技术" aria-label="Permalink to &quot;核心技术&quot;">​</a></h2><h3 id="编程语言" tabindex="-1">编程语言 <a class="header-anchor" href="#编程语言" aria-label="Permalink to &quot;编程语言&quot;">​</a></h3><ul><li><strong>Java</strong> (Spring Boot)</li><li><strong>Python</strong> (Django, Flask, FastAPI)</li><li><strong>JavaScript/Node.js</strong> (Express, NestJS)</li><li><strong>C#</strong> (.NET Core)</li><li><strong>Go</strong> (Gin, Echo)</li><li><strong>Ruby</strong> (Ruby on Rails)</li><li><strong>PHP</strong> (Laravel, Symfony)</li></ul><h3 id="数据库" tabindex="-1">数据库 <a class="header-anchor" href="#数据库" aria-label="Permalink to &quot;数据库&quot;">​</a></h3><ul><li><strong>关系型</strong>：MySQL, PostgreSQL, SQL Server, Oracle</li><li><strong>NoSQL</strong>：MongoDB, Redis, Cassandra, Elasticsearch</li></ul><h3 id="api开发" tabindex="-1">API开发 <a class="header-anchor" href="#api开发" aria-label="Permalink to &quot;API开发&quot;">​</a></h3><ul><li>RESTful API</li><li>GraphQL</li><li>gRPC (高性能RPC框架)</li></ul><h2 id="现代后端技术栈" tabindex="-1">现代后端技术栈 <a class="header-anchor" href="#现代后端技术栈" aria-label="Permalink to &quot;现代后端技术栈&quot;">​</a></h2><h3 id="服务器与部署" tabindex="-1">服务器与部署 <a class="header-anchor" href="#服务器与部署" aria-label="Permalink to &quot;服务器与部署&quot;">​</a></h3><ul><li><strong>Web服务器</strong>：Nginx, Apache</li><li><strong>容器化</strong>：Docker, Kubernetes</li><li><strong>云服务</strong>：AWS, Azure, Google Cloud, 阿里云</li></ul><h3 id="中间件与技术" tabindex="-1">中间件与技术 <a class="header-anchor" href="#中间件与技术" aria-label="Permalink to &quot;中间件与技术&quot;">​</a></h3><ul><li><strong>消息队列</strong>：RabbitMQ, Kafka</li><li><strong>缓存系统</strong>：Redis, Memcached</li><li><strong>搜索引擎</strong>：Elasticsearch</li></ul><h3 id="开发工具" tabindex="-1">开发工具 <a class="header-anchor" href="#开发工具" aria-label="Permalink to &quot;开发工具&quot;">​</a></h3><ul><li><strong>版本控制</strong>：Git</li><li><strong>CI/CD</strong>：Jenkins, GitHub Actions, GitLab CI</li><li><strong>监控</strong>：Prometheus, Grafana</li></ul><h2 id="关键概念" tabindex="-1">关键概念 <a class="header-anchor" href="#关键概念" aria-label="Permalink to &quot;关键概念&quot;">​</a></h2><ul><li><strong>认证与授权</strong> (JWT, OAuth)</li><li><strong>微服务架构</strong></li><li><strong>分布式系统</strong></li><li><strong>数据库设计与优化</strong></li><li><strong>API安全</strong> (防SQL注入, XSS, CSRF等)</li></ul><h2 id="学习路径建议" tabindex="-1">学习路径建议 <a class="header-anchor" href="#学习路径建议" aria-label="Permalink to &quot;学习路径建议&quot;">​</a></h2><ol><li>选择一门后端语言深入学习</li><li>掌握数据库基础知识和SQL</li><li>学习Web框架和API开发</li><li>理解HTTP协议和网络基础</li><li>实践项目部署和DevOps基础</li></ol><h2 id="学习文档" tabindex="-1">学习文档 <a class="header-anchor" href="#学习文档" aria-label="Permalink to &quot;学习文档&quot;">​</a></h2><ul><li><a href="/docs/backend/php/">PHP</a></li><li><a href="/docs/backend/node/">Node</a></li><li><a href="/docs/backend/java/">Java</a></li><li><a href="/docs/backend/go/">Go</a></li></ul>',22)]))}const b=l(i,[["render",t]]);export{u as __pageData,b as default};
