import{_ as e,c as a,o,ag as r}from"./chunks/framework.ko2zIC2c.js";const f=JSON.parse('{"title":"前端学习路线","description":"前端学习路线, WEB前端教程","frontmatter":{"title":"前端学习路线","description":"前端学习路线, WEB前端教程"},"headers":[],"relativePath":"docs/road/core/frontend.md","filePath":"docs/road/core/frontend.md","lastUpdated":1753665350000}'),d={name:"docs/road/core/frontend.md"};function l(n,t,s,c,i,h){return o(),a("div",null,t[0]||(t[0]=[r('<h1 id="前端学习路线" tabindex="-1">前端学习路线 <a class="header-anchor" href="#前端学习路线" aria-label="Permalink to &quot;前端学习路线&quot;">​</a></h1><p>前端开发是指创建网站或Web应用程序用户界面(UI)的部分，主要涉及以下技术：</p><h2 id="核心技术" tabindex="-1">核心技术 <a class="header-anchor" href="#核心技术" aria-label="Permalink to &quot;核心技术&quot;">​</a></h2><ul><li><strong>HTML</strong> - 网页内容结构</li><li><strong>CSS</strong> - 样式和布局设计</li><li><strong>JavaScript</strong> - 交互和动态功能</li></ul><h2 id="现代前端技术栈" tabindex="-1">现代前端技术栈 <a class="header-anchor" href="#现代前端技术栈" aria-label="Permalink to &quot;现代前端技术栈&quot;">​</a></h2><ul><li><strong>框架/库</strong>：React, Vue, Angular, Svelte</li><li><strong>CSS预处理器</strong>：Sass, Less</li><li><strong>构建工具</strong>：Webpack, Vite, Rollup</li><li><strong>包管理器</strong>：npm, yarn, pnpm</li><li><strong>TypeScript</strong> - JavaScript的超集，添加类型系统</li></ul><h2 id="进阶领域" tabindex="-1">进阶领域 <a class="header-anchor" href="#进阶领域" aria-label="Permalink to &quot;进阶领域&quot;">​</a></h2><ul><li><strong>响应式设计</strong> - 适配不同设备</li><li><strong>Web性能优化</strong></li><li><strong>PWA(渐进式Web应用)</strong></li><li><strong>WebAssembly</strong></li><li><strong>前端测试</strong> (Jest, Cypress等)</li></ul><h2 id="学习文档" tabindex="-1">学习文档 <a class="header-anchor" href="#学习文档" aria-label="Permalink to &quot;学习文档&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">技术栈</th><th>简介</th><th>难度</th></tr></thead><tbody><tr><td style="text-align:left;"><a href="/docs/frontend/html/">HTML</a></td><td><code>HTML(HyperText Markup Language)</code>是用于创建网页和<code>Web</code>应用程序的标准标记语言, 定义了网页的结构和内容</td><td>🌟</td></tr><tr><td style="text-align:left;"><a href="/docs/frontend/css/">CSS</a></td><td><code>CSS(Cascading Style Sheets)</code>是用来控制网页表现和布局的样式语言, 与<code>HTML</code>配合使用,实现内容与样式的分离</td><td>🌟</td></tr><tr><td style="text-align:left;"><a href="/docs/frontend/js/">JavaScript</a></td><td><code>JavaScript</code>, 简称<code>JS</code>, 是一种轻量级的解释型编程语言, 主要用于网页交互和动态内容开发, 是 Web 三大核心技术之一</td><td>🌟🌟</td></tr><tr><td style="text-align:left;"><a href="/docs/frontend/ts/">TypeScript</a></td><td><code>TypeScript</code>是<code>JavaScript</code>的超集, 由微软开发并维护, 它添加了静态类型系统和一些面向对象编程特性, 最终会被编译成纯<code>JavaScript</code>代码</td><td>🌟🌟🌟</td></tr><tr><td style="text-align:left;"><a href="/docs/frontend/vue/">Vue</a></td><td><code>Vue.js</code>是一套用于构建用户界面的渐进式框架, 与其他大型框架不同, <code>Vue</code>被设计为可以自底向上逐层应用</td><td>🌟🌟🌟</td></tr><tr><td style="text-align:left;"><a href="/docs/frontend/vue/">React</a></td><td><code>React</code>是<code>Facebook</code>开发的一个用于构建用户界面的<code>JavaScript</code>库, 专注于组件化和高效的`DOM更新</td><td>🌟🌟🌟</td></tr></tbody></table>',10)]))}const p=e(d,[["render",l]]);export{f as __pageData,p as default};
